mapper.delete:

delete from s_b_abnormal_info
where farm_id = #{farmId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and alarm_code = #{alarmCode,jdbcType=VARCHAR}

mapper.update:

update s_b_abnormal_info
set status = #{status,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=TIMESTAMP},
  standar_delay = #{standarDelay,jdbcType=INTEGER},
  deal_status = #{dealStatus,jdbcType=VARCHAR},
  deal_delay = #{dealDelay,jdbcType=INTEGER},
  deal_time = #{dealTime,jdbcType=TIMESTAMP},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where farm_id = #{farmId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and alarm_code = #{alarmCode,jdbcType=VARCHAR}

mapper.select:

select farm_id, house_id, alarm_code, status, start_time, standar_delay, deal_status, 
deal_delay, deal_time, modify_time
from s_b_abnormal_info
where farm_id = #{farmId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and alarm_code = #{alarmCode,jdbcType=VARCHAR}
