mapper.delete:

delete from s_b_dayage_temp_sub
where uid_num = #{uidNum,jdbcType=INTEGER}
  and day_age = #{dayAge,jdbcType=INTEGER}
  and record_datetime = #{recordDatetime,jdbcType=TIMESTAMP}

mapper.select:

select uid_num, day_age, record_datetime, set_temp, high_alarm_temp, low_alarm_temp, 
set_humidity, memo, create_person, create_date, create_time, modify_person, modify_date, 
modify_time
from s_b_dayage_temp_sub
where uid_num = #{uidNum,jdbcType=INTEGER}
  and day_age = #{dayAge,jdbcType=INTEGER}
  and record_datetime = #{recordDatetime,jdbcType=TIMESTAMP}
