mapper.select:

select house_breed_id, day_age, week_age, growth_date, is_history, death_am, death_pm, 
culling_am, culling_pm, cur_cd, acc_cd, cur_feed, acc_feed, cur_weight, cur_amount, 
ytd_amount, cur_water, acc_water, cur_lay_num, acc_lay_num, cur_lay_weight, acc_lay_weight, 
cur_lay_broken, acc_lay_broken, int_bak1, int_bak2, int_bak3, num_bak1, num_bak2, 
num_bak3, var_bak1, var_bak2, var_bak3, date_bak1, date_bak2, date_bak3, create_person, 
create_date, create_time, modify_person, modify_date, modify_time
from s_b_layer_breed_detail
where house_breed_id = #{houseBreedId,jdbcType=INTEGER}

    AND day_age  >= #{dayAge1}


    AND day_age  <= #{dayAge2}

update.foreach:
update s_b_layer_breed_detail
death_am = #{item.deathAm,jdbcType=INTEGER}, death_pm = #{item.deathPm,jdbcType=INTEGER}, culling_am = #{item.cullingAm,jdbcType=INTEGER}, culling_pm = #{item.cullingPm,jdbcType=INTEGER}, cur_cd = #{item.curCd,jdbcType=INTEGER}, acc_cd = #{item.accCd,jdbcType=INTEGER}, cur_feed = #{item.curFeed,jdbcType=DECIMAL}, acc_feed = #{item.accFeed,jdbcType=DECIMAL}, cur_weight = #{item.curWeight,jdbcType=DECIMAL}, cur_amount = #{item.curAmount,jdbcType=INTEGER}, ytd_amount = #{item.ytdAmount,jdbcType=INTEGER}, cur_water = #{item.curWater,jdbcType=DECIMAL}, acc_water = #{item.accWater,jdbcType=DECIMAL}, cur_lay_num = #{item.curLayNum,jdbcType=INTEGER}, acc_lay_num = #{item.accLayNum,jdbcType=INTEGER}, cur_lay_weight = #{item.curLayWeight,jdbcType=DECIMAL}, acc_lay_weight = #{item.accLayWeight,jdbcType=DECIMAL}, cur_lay_broken = #{item.curLayBroken,jdbcType=INTEGER}, acc_lay_broken = #{item.accLayBroken,jdbcType=INTEGER}, int_bak1 = #{item.intBak1,jdbcType=INTEGER}, int_bak2 = #{item.intBak2,jdbcType=INTEGER}, int_bak3 = #{item.intBak3,jdbcType=INTEGER}, num_bak1 = #{item.numBak1,jdbcType=DECIMAL}, num_bak2 = #{item.numBak2,jdbcType=DECIMAL}, num_bak3 = #{item.numBak3,jdbcType=DECIMAL}, modify_person = #{item.modifyPerson,jdbcType=INTEGER}, modify_date = #{item.modifyDate,jdbcType=TIMESTAMP}, modify_time = #{item.modifyTime,jdbcType=TIMESTAMP}
where house_breed_id = #{item.houseBreedId,jdbcType=INTEGER} and day_age = #{item.dayAge,jdbcType=INTEGER}
