mapper.delete:

delete from s_b_layer_house_alarm
where farm_id = #{farmId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}

mapper.select:

select farm_id, house_id, alarm_delay, temp_cpsation, temp_cordon, alarm_way, target_temp, 
min_temp, max_temp, alarm_probe, point_alarm, create_person, create_date, create_time, 
modify_person, modify_date, modify_time
from s_b_layer_house_alarm
where farm_id = #{farmId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
