  #{createPerson,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modifyPerson,jdbcType=INTEGER}, #{modifyDate,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}
  )

mapper.select:

