mapper.delete:

delete from s_b_call_alarm
where main_code = #{mainCode,jdbcType=VARCHAR}
  and alarm_id = #{alarmId,jdbcType=INTEGER}

mapper.update:

update s_b_call_alarm
set alarm_code = #{alarmCode,jdbcType=VARCHAR},
  alarm_name = #{alarmName,jdbcType=VARCHAR},
  alarm_status = #{alarmStatus,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where main_code = #{mainCode,jdbcType=VARCHAR}
  and alarm_id = #{alarmId,jdbcType=INTEGER}

mapper.select:

select main_code, alarm_id, alarm_code, alarm_name, alarm_status, create_time, modify_time
from s_b_call_alarm
where main_code = #{mainCode,jdbcType=VARCHAR}
  and alarm_id = #{alarmId,jdbcType=INTEGER}
